import DNA_to_RNA
import RNA_to_protein


def DNA_splicing(dna, introns):
    for intron in introns:
        exons = dna.replace(intron, '')

    exons_to_rna = DNA_to_RNA.dna_to_rna(exons)
    gene = RNA_to_protein.RNA_to_protein(exons_to_rna)

    return gene


